"""
Standard SSH key exchange ("kex" if you wanna sound cool).  Diffie-Hellman of
3072 bit key halves, using a known "p" prime and "g" generator.
"""

from paramiko.kex_group1_sha1 import KexGroup1Sha1
from hashlib import sha512


class KexGroup15Sha512(KexGroup1Sha1):

    # http://tools.ietf.org/html/rfc3526#section-4
    P = (
          # noqa
    )
    G = 2

    name = "diffie-hellman-group15-sha512"
    hash_algo = sha512
